{!REQUIRESCRIPT("/soap/ajax/10.0/connection.js")} 
 {!REQUIRESCRIPT("/soap/ajax/14.0/apex.js")} 
 {!REQUIRESCRIPT('/support/console/31.0/integration.js')}; 

 var records = {!GETRECORDIDS($ObjectType.Contact)}; 
 var hasShortCodeNumber = sforce.apex.execute("LiveText.RetrievePreferredPhoneNumber","agentHasShortcodeNumberAssigned",{});
 if (records[0] == null){ 
 alert("Please select at least one contact");
 }else if((records.length > 25) && hasShortCodeNumber=="false"){ 
 alert("A maximum of 25 text messages may be sent at once. Please select fewer recipients and try again."); 
 } 
 else{ 
 var packagePrefix = "LiveText__"; 
 var recordType = "account"; 
 var recordIds = "";

 for (var n in records) { 
 if(n == 'remove' || n == 'indexOf') continue; 
 recordIds = recordIds + records[n]; 

 if (n != records.length-1) { 
 recordIds = recordIds + ","; 
 } 
 }

 var storageId = sforce.apex.execute("LiveText.RetrievePreferredPhoneNumber","saveToStorage",{stringToSave:recordIds});
 var urlStart = 'https://'+window.location.hostname+'/apex/LiveText__LiveTextBulkSms?recordType='+recordType+'&storageId='+storageId; 

 if(sforce.console.isInConsole()) { 
 sforce.console.openPrimaryTab(null, urlStart, true);
 } else { 
 window.top.location.href = urlStart; 
 } 
 }

