			var records = {!GETRECORDIDS($ObjectType.Account)}; 
			if (records[0] == null) 
			{ 
				alert("Please select at least one account") 
			} 
			else{ 
				var packagePrefix = "LiveText__"; 
				var recordType = "account"; 
				var recordIds = ""; 
				var optOutField = ''; 
				var optOutDefaultValue = 'yes'; 
				var smsType = ''; 
				var userId = '{!$User.Id}'; 
				var elementIdVar = ''; 
				var showUserDropDown = false; 
				var dialogTitle = ''; 
				var queryString = ''; 

				for (var n in records) { 
					if(n == 'remove' || n == 'indexOf') continue; 
					recordIds = recordIds + records[n]; 

					if (n != records.length-1) { 
						recordIds = recordIds + ","; 
					} 
				} 

				var e = document.querySelector('select[name$="fcf"]');
				var selectedView = e.options[e.selectedIndex].value; 

				var returnURL = '/001?fcf='+ selectedView + '&rolodexIndex=-1&page=1'; 

				var urlStart = 'https://'+window.location.hostname+'/apex/LiveText__LiveTextOptinStatusPage?recordType='+recordType+
				 				'&records='+recordIds+'&retURL='+returnURL; 

				if(sforce.console.isInConsole()) { 
					sforce.console.openPrimaryTab(null, urlStart + '&inConsole=yes&optType=0', true, 'Opt-out for Text Messages'); 
				}else{ 
					window.top.location.href = urlStart + '&inConsole=no&optType=0'; 
				} 

				console.log('Record Ids are '+ recordIds); 
			}

